import React, { Component } from 'react';

export default class AddAuthor extends React.Component{
  constructor() {
    super();

    this.state = {
      name: '',
      surname: '',
    };
}

changeState = e => {
  const {value ,name } = e.target;
  this.setState({
    [name]: value
  });
}
  render(){
    const {addAuthor} = this.props;
    const {name , surname } = this.state;

    return(
      <form onSubmit={() => addAuthor(this.state)}>
      <div className="sing-in">
        <div className="sing-in-label">
          <div>
            <label>Name</label><br/>
            <input type="text" name="name" value={name} onChange={this.changeState}/>
          </div>
        <div>
          <label>Surname</label><br/>
          <input type ="text" name="surname" value={surname} onChange={this.changeState}/>
        </div>


        </div>
        <input type="submit" value="Add Author" />
      </div>
    </form>
    );
}}
