import { Component, default as React } from 'react';
import './book.css';

export default class Book extends React.Component {

  constructor() {
    super();

    this.state = {
        title: '',
        body: '',
       
    };
  }

componentWillMount(){
  const {book} = this.props;

  if(book) {
    const {id, title, body } = book;

    this.setState({
      id: id,
      title: title,
      body:body
    });
  }
}

changeState = e => {
  const {value ,name } = e.target;
  this.setState({
    [name]: value
  });
}

render() {
  const {addEditBook, book} = this.props;
  const {title,body } = this.state;
 
  const isEditMode = this.props.book ? true : false;

  return(
    <form onSubmit={() => addEditBook(this.state)}>
     <div className="book">
        <div className="book-label">
          <p>
            <label>Title</label><br/>
            <input type="text" name="title" value={title} onChange={this.changeState} disabled={isEditMode}/>
          </p>
        
        <p>
          <label>Description</label><br/>
          <input type ="text" name="body" value={body} onChange={this.changeState}  disabled={isEditMode}/><br/>
        </p>
        
        </div>
        
        
        <input type="submit" value="Add Book" />
</div>
    </form>
  );
}};
