import React, { Component } from 'react';
import { BrowserRouter, Redirect, Route, Link } from 'react-router-dom';
import './App.css';

import BookList from './components/book/book';
import Book from './components/book/book';
import AddAuthor from './components/AddAuthor';


class App extends Component {
  async AddBook(data) {
    const response = await fetch('https://localhost:3000/books', {
      method: 'POST',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({data})
    });

    const result = await response.json();
  }

  async AddAuthor(data) {
    const response = await fetch('https://localhost:3000/autors', {
      method: 'POST',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({data})
    });

    const result = await response.json();
  }

  render() {
    return (
      <BrowserRouter>
        <div className="App">
          <div className='header'>
            <Link  to='/books' className="link">Books</Link>
            <Link  to='/addAuthor'> SignIn</Link>
            <Link to='/login'>LogIn</Link>
          </div>

          <Route exact path='/books' render={() => <BookList />} />

          <Route exact path='/login' render={() => <addAuthorForm />} />
          <Route exact path='/book/:bookId' component={Book} />
          <Route exact path='/book' render={() => <Book />}/>
          <Route exact path='/' render={() => <Redirect to='/book' />} />
        </div>
      </BrowserRouter>
    );
  }
}

export default App;
