import React, { Component } from 'react';
import { BrowserRouter, Redirect, Route, Link } from 'react-router-dom';
import './App.css';

import BooksList from './components/book/book';
import Book from './components/book/book';
import AddAuthor from './components/addauthor';
import AuthorLogin from './components/author_login';

class App extends Component{

async AddNewAuthor(data) {
  const post_request = await fetch('https://localhost:3000/autors', {
    method: 'POST',
    headers: {
      'Accept': 'application/json',
      'Content-Type': 'application/json'
    },
    body: JSON.stringify({data})
  });

  const result = await post_request.json();
}


  async AddNewBook(data) {
    const response = await fetch('https://localhost:3000/books', {
      method: 'POST',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({data})
    });

    const result = await response.json();
  }


  render() {
    return (
      <BrowserRouter>
        <div className="main">
          <div className='header'>
            <Link  to='/books' className="title"> <h1>Book Store</h1></Link>
            <div className="nav">
              <Link  to='/signin'> SignIn</Link>
              <Link to='/login'>LogIn</Link>
            </div>
          </div>

          <Route exact path='/books' render={() => <BookList />} />
          <Route exact path='/authors' render={() => <AuthorList />} />
          <Route exact path='/signin' render={() => isAuth ? <Redirect to='/books' /> : <SignInForm />} />
          <Route exact path='/login' render={() => isAuth ? <Redirect to='/books' /> : <LogInForm />} />
          <Route exact path='/book/:bookId' component={Book} />
          <Route exact path='/book' component={Book} />
          <Route exact path='/' render={() => <Redirect to='/books' />} />
</div>

      </BrowserRouter>
    );
  }
}

export default App;
