import React from 'react';
import './book.css';

export default class Book extends React.Component {

  constructor() {
    super();

    this.state = {
        title: '',
        body: '',
    };
  }
  changeState = e => {
    const {value ,name } = e.target;
    this.setState({
      [name]: value
    });
  }
  

componentWillMount(){
  const {book} = this.props;

  if(book) {
    const {id, title, body } = book;

    this.setState({
      id: id,
      title: title,
      book: book,
     
    });
  }
}

render() {
  const {AddNewBook, book} = this.props;
  const {title,body } = this.state;
  const isEditMode = this.props.book ? true : false;

  return(
    <form onSubmit={() => AddNewBook(this.state)}>
      <div className="book">
        <div className="book-label">
          <div>
            <label>Title</label><br/>
            <input type="text" name="title" value={title} onChange={this.changeState} disabled={isEditMode}/>
          </div>
        
        </div>
        <label>Description</label><br/>
        <textarea className="desc" type ="text" name="description" value={body} onChange={this.changeState}  disabled={isEditMode}  /><br/>
        <input type="submit" value="Create Your Book!" />
      </div>
    </form>
  );
}};
