import Book from './book/book';
export default class SingIn extends React.Component{
  constructor() {
    super();

    this.state = {
      name: '',
      surname: '',
    };
    this.addEditBook = this.addEditBook.bind(this);
  }

async addEditBook() {
  const rawResponse = await fetch('https://localhost:3000/autors', {
    method: 'POST',
    headers: {
      'Accept': 'application/json',
      'Content-Type': 'application/json'
    },
    body: JSON.stringify({
      name:this.state.name,
      surname:this.state.surname

    })
  })
}


  render(){


    return(
      <form onSubmit={() => signInAuthor(this.state)}>
      <div >
        <div className="sing-in-label">
          <div>
            <label>Name</label><br/>
            <input type="text" name="name" value={this.state.name} onChange={(event) => {this.setState({name: event.target.value})}}/>
          </div>
        <div>
          <label>Surname</label><br/>
          <input type ="text" name="surname" value={this.state.surname} onChange={(event) => {this.setState({surname: event.target.value})}}/>
        </div>
        </div>

       <input type="submit" value="Create" onClick= {
                        this.addEditBook
